package exercise5_27;

public class Segment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i =  0, j = 0, k = 0;
		
			for (i = 1; i <= 5; i++)
			{
				for (j = 1; j <= 3; j++)
				{
					for (k = 1; k <= 4; k++)
						
						System.out.print('*');
					
							System.out.println();
				} // end inner for
				
			System.out.println();
			
			}
			
	}

}
