package exercise5_27;

public class ProgramSegment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 5; i++)
		{
			for (int j = 1; j <= 3; j++)
			{
				for (int k = 1; k <= 4; k++) 
					System.out.print('*');
				System.out.println();
			} // end inner for
		System.out.println();
		} // end outer for
		
		
	}

}
